<script>
	import {
		login
	} from "@/components/login/index.js";
	import {
		showToast
	} from '@/components/showToast/showToast'

	export default {
		onLaunch() {
			let token = login.isLogin()
			if (!token) {
				login.logOut()
			} else {
				// 是否上传过资质
				login.certification()
				login.smallHb()
				login.userInfo()
			}
		},
		onShow() {},
		onHide() {}
	}
</script>

